import createStore from "unistore";

export const store = createStore({
  count: 0,
  is_login: false,
  user_name: "",
  email: ""
});

export const actions = store => ({
  login(state, uname, email) {
    return { is_login: true, user_name: uname, email: email };
  },

  increment(state) {
    return { count: state.count + 1 };
  }
});
