import React from "react";
import axios from "axios";

const apiKey = "fa1283cfda8e488c98801d4f17560869";
const baseUrl = "https://newsapi.org/v2/";
const urlHeadline = baseUrl + "top-headlines?country=us&apiKey" + apiKey;
export class Headline extends React.Component {
  constructor(props) {
    super(props);
    this.state = { news: [] };
  }

  componentWillMount() {
    const self = this;
    axios
      .get(urlHeadline)
      .then(function(response) {
        self.setState({ news: response.data.articles });
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  render() {
    return (
      <div>
        <img
          src={this.state.news[0].urlToImage}
          style={{
            width: 600,
            alignSelf: "center",
            padding: 15
          }}
        />
        <h3>{this.state.news[0].title}</h3>
        <p style={{ fontSize: 10, color: "grey" }}>
          Published : {this.state.news[0].publishedAt}
        </p>
        <p>{this.state.news[0].content}</p>
      </div>
    );
  }
}
